import './App.css';
import React from "react";
import { Container, makeStyles, Tab, Tabs } from '@material-ui/core';
import HomePageComponent from './components/HomePageComponent';
import OnePost from './components/OnePost.js';
import PropTypes from 'prop-types';

const useStyles = makeStyles({
  root: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px',
  },
});

export default function App(){

  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue)
  }

  return (
    <Container>
        <Tabs
          value={value}
          indicatorColor="primary"
          textColor="primary"
          onChange={handleChange}
          centered
          >
              <Tab label="Home" value={0}/>
              <Tab label="About Me" value={1}/>
              <Tab label="Papers" value={2}/>
              <Tab label="Research" value={3}/>
              <Tab label="Contacts" value={4}/>
        </Tabs>
        {value !== undefined && value==0 && <HomePageComponent/>}
        {value !== undefined && value==1 && <div>About Me</div>}
        {value !== undefined && value==2 && <div>Papers</div>}
        {value !== undefined && value==3 && <div>Research</div>}
        {value !== undefined && value==4 && <div>Contacts</div>}
    </Container>
  );
}
